function permAlone(str) {
  return permutor(str).filter(function(perm) {
    return !perm.match(/(.)\1/g);
  }).length;
}

function permutor(str) {
  // http://staff.roguecc.edu/JMiller/JavaScript/permute.html
  //permArr: 全局数组：包含排列的列表 \ n  //usedChars: 全局实用数组，其中一个列表包含 "currently-in-use"字符  var permArr = [], usedChars = [];
  function permute(input) {
    // 将输入参数转换成一个字符串数组 （每个字符都有一个元素）
    var i, ch, chars = input.split("");
    for (i = 0; i < chars.length; i++) {
      // 从字符数组中获取并删除索引为"i"的字符 
      ch = chars.splice(i, 1);
      // 将删除的字符增加的使用字符的末尾 
      usedChars.push(ch);
      // 当char数组中没有其他字符添加时，添加使用的chars列表来排列排列 
      if (chars.length === 0) permArr[permArr.length] = usedChars.join("");
      // 从char数组发送字符（减去上面删除的字符）进行置换 
      permute(chars.join(""));
      // 将删除的数组添加回char数组中的原位置 
      chars.splice(i, 0, ch);
      //删除使用过的字符数组末尾使用的最后一个字符 
      usedChars.pop();
    }
  }
  permute(str);
  return permArr;
}

permAlone('aab');

function pairwise(arr, arg) {
  var sum = 0;
  arr.forEach(function(e, i, a) {
    if (e != null) {      
      var diff = arg-e;
      a[i] = null;
      var dix = a.indexOf(diff);
      if (dix !== -1) {
        sum += dix;
        sum += i;
        a[dix] = null;
      }  
    }
  });
  return sum;
}

pairwise([1,4,2,3,0,5], 7);
